import React from "react";
import { DragDropContext, DropTarget, DragSource } from "react-dnd";
import HTML5Backend from "react-dnd-html5-backend";
import update from "immutability-helper";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faAdd, faEdit, faTrash } from "@fortawesome/free-solid-svg-icons";
import { confirmAlert } from 'react-confirm-alert'
import 'react-confirm-alert/src/react-confirm-alert.css';
import APIURL from "./APIUrl";
import axios from "axios";
import { Button, Modal, ModalBody, ModalFooter, ModalHeader } from "react-bootstrap";

const quarters = ["q1", "q2", "q3", "q4"];
const labelsMap = {
  q1: "Q1",
  q2: "Q2",
  q3: "Q3",
  q4: "Q4"
};

const classes = {
  board: {
    display: "flex",
    margin: "0 auto",
    width: "100vw",
    fontFamily: 'Arial, "Helvetica Neue", sans-serif',
    backgroundColor: "#FCC8B2"
  },
  column: {
    minWidth: 350,
    width: "18vw",
    height: "70vh auto",
    margin: "0 auto",
    // backgroundColor: "#FCC8B2"
  },
  columnHead: {
    textAlign: "center",
    padding: 10,
    fontSize: "1.2em",
    backgroundColor: "#C6D8AF"
  },
  item: {
    padding: 10,
    margin: 10,
    fontSize: "0.8em",
    cursor: "pointer",
    backgroundColor: "white"
  }
};

class Kanban extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      tasks: [], error: "", show: false,
      modal: false,
      selectedId: 0, taskDescription: "",
    };
    this.handleChange = this.handleChange.bind(this);
    this.edit = this.edit.bind(this);
  }
  deletedocument = (id) => {
    confirmAlert({
      message: 'Are you sure you wish to delete this task?',
      buttons: [
        {
          label: 'Yes',
          onClick: () => this.getDeleteUrl(id)
        },
        {
          label: 'No',
          // onClick: () => alert('Click No')
        }
      ]
    });
  }
  getDeleteUrl = (id) => {
    const formData = new FormData();
    formData.append('selectedId', id)
    axios.post(APIURL + '/deleteTasks', formData)
      .then(response => {
        if (response.status === 200) {
          confirmAlert({
            message: 'Task Details Successfully Deleted',
            buttons: [{ label: 'Close', }]
          });
          this.componentDidMount();
        }
      },
        (error) => {
          confirmAlert({
            message: 'Task Details Failed to Delete!',
            buttons: [{ label: 'Close', }]
          });
        });
  }
  handleChange(event) {
    const name = event.target.name;
    const value = event.target.value;

    this.setState({
      [name]: value
    })
  }
  handleModal(id, name, label) {
    if (id >= 0) {
      this.setState({ show: !this.state.show, modelLabel: label, selectedId: id, taskDescription: "" })
      this.getDetails(id);
    }
  }
  getDetails(id) {
    const options = {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(),
    }
    fetch(APIURL + '/getTaskDetails/' + id, options)
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            taskDescription: result[0].title,
          });
        },
        (error) => {
          this.setState({ error });
        }
      )
  }
  edit(id) {
    this.setState({ show: !this.state.show, modelLabel: name })
    this.getDetails(id);
  }

  save(status) {
    const formData = new FormData();
    formData.append('status', status)
    formData.append('title', this.state.taskDescription)
    axios.post(APIURL + '/addTask', formData)
      .then(response => {
        if (response.status === 200) {
          confirmAlert({
            message: 'Task Details Successfully Created',
            buttons: [{ label: 'Close', }]
          });
          this.setState({ show: !this.state.show, modelLabel: name })
          this.componentDidMount();
        }
      },
        (error) => {
          confirmAlert({
            message: 'Task Details Failed to Create!',
            buttons: [{ label: 'Close', }]
          });
        });
  }
  updateTask(id, name) {
    const formData = new FormData();
    formData.append('status', name)
    formData.append('id', id)
    formData.append('title', this.state.taskDescription)
    axios.post(APIURL + '/updateTask', formData)
      .then(response => {
        if (response.status === 200) {
          confirmAlert({
            message: 'Task Details Successfully Updated',
            buttons: [{ label: 'Close', }]
          });
          this.setState({ show: !this.state.show })
          this.componentDidMount();
        }
      },
        (error) => {
          confirmAlert({
            message: 'Task Details Failed to Update!',
            buttons: [{ label: 'Close', }]
          });
        });
  }
  componentDidMount() {
    const options = {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(this.state),
    }
    fetch(APIURL + '/getList', options)
      .then(res => res.json())
      .then(
        (result) => {
          console.log(result);
          this.setState({
            tasks: result
          });
        },
        (error) => {
          this.setState({ error });
        }
      )
  }
  update = (id, status) => {
    const formData = new FormData();
    formData.append('status', status)
    formData.append('id', id)
    axios.post(APIURL + '/moveTask', formData)
      .then(response => {
        if (response.status === 200) {
          this.componentDidMount();
        }
      },
        (error) => {
          confirmAlert({
            message: 'Failed to move',
            buttons: [{ label: 'Close', }]
          });
        });
  };

  render() {
    const { tasks, error } = this.state;
    if (error) {
      return (
        <div>Data Not Available</div>
