import React from "react";
import { DragDropContext, DropTarget, DragSource } from "react-dnd";
import HTML5Backend from "react-dnd-html5-backend";
import update from "immutability-helper";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faDeleteLeft, faEdit } from "@fortawesome/free-solid-svg-icons";
import { Button, Modal, ModalBody, ModalFooter } from 'react-bootstrap'
import ModalHeader from 'react-bootstrap/esm/ModalHeader'
import { confirmAlert } from 'react-confirm-alert'
import 'react-confirm-alert/src/react-confirm-alert.css';
import APIURL from "./APIUrl";

const quarters = ["q1", "q2", "q3", "q4"];
const labelsMap = {
  q1: "Q1",
  q2: "Q2",
  q3: "Q3",
  q4: "Q4"
};

const classes = {
  board: {
    display: "flex",
    margin: "0 auto",
    width: "90vw",
    fontFamily: 'Arial, "Helvetica Neue", sans-serif'
  },
  column: {
    minWidth: 350,
    width: "18vw",
    height: "75vh",
    margin: "0 auto",
    backgroundColor: "#FCC8B2"
  },
  columnHead: {
    textAlign: "center",
    padding: 10,
    fontSize: "1.2em",
    backgroundColor: "#C6D8AF"
  },
  item: {
    padding: 10,
    margin: 10,
    fontSize: "0.8em",
    cursor: "pointer",
    backgroundColor: "white"
  }
};

class Kanban extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      tasks: [], error: "", show: false,
      modal: false,
      selectedId: 0, taskDescription: "",
    };
    this.handleChange = this.handleChange.bind(this);
    this.edit = this.edit.bind(this);
    this.openModal = this.openModal.bind(this);
  }
  deletedocument = (id) => {
    confirmAlert({
      message: 'Are you sure you wish to delete this task?',
      buttons: [
        {
          label: 'Yes',
          onClick: () => this.getDeleteUrl(id)
        },
        {
          label: 'No',
          // onClick: () => alert('Click No')
        }
      ]
    });
  }
  getDeleteUrl = (id) => {
    const formData = new FormData();
    formData.append('selectedId', id)
    axios.post(APIURL + '/deleteTasks', formData)
      .then(response => {
        if (response.status === 200) {
          confirmAlert({
            message: 'Task Details Successfully Deleted',
            buttons: [{ label: 'Close', }]
          });
          this.componentDidMount();
        }
      },
        (error) => {
          confirmAlert({
            message: 'Task Details Failed to Delete!',
            buttons: [{ label: 'Close', }]
          });
        });
  }
  handleChange(event) {
    const name = event.target.name;
    const value = event.target.value;

    this.setState({
      [name]: value
    })
  }
  handleModal(id, name) {
    if (id >= 0) {
      this.setState({ show: !this.state.show, modelLabel: name })
      this.getDetails(id);
    }
  }
  getDetails(id) {
    const options = {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(),
    }
    console.log(this.state)
    fetch(APIURL + '/getTaskDetails/' + id, options)
      .then(res => res.json())
      .then(
        (result) => {
          console.log(result[0].title);
          this.setState({
            taskDescription: result[0].title,
          });
        },
        (error) => {
          this.setState({ error });
        }
      )
  }
  edit(id) {
    console.log(id);
  }
  openModal() {
    this.setState({ modalIsOpen: true });
  }
  componentDidMount() {
    const options = {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(this.state),
    }
    fetch(APIURL + '/getList', options)
      .then(res => res.json())
      .then(
        (result) => {
          console.log(result);
          this.setState({
            tasks: result
          });
        },
        (error) => {
          this.setState({ error });
        }
      )
  }
  update = (id, status) => {
    alert(id + ":" + status);
    const { tasks } = this.state;
    const task = tasks.find(task => task._id === id);
    task.status = status;
    const taskIndex = tasks.indexOf(task);
    const q2Tasks = update(tasks, {
      [taskIndex]: { $set: task }
    });
    this.setState({ tasks: q2Tasks });
  };

  render() {
    const { tasks, error, taskDescription } = this.state;
    console.log(taskDescription);
    if (error) {
      return (
        <div>Data Not Available</div>
      )
    } else {
      return (
        <main>
          <header><h4>Company’s Strategies</h4> </header>
          <section style={classes.board}>
            {quarters.map(channel => (
              <KanbanColumn status={channel}>
                <div style={classes.column}>
                  <div style={classes.columnHead}>{labelsMap[channel]}</div>
                  <div>
                    {tasks
                      .filter(item => item.status === channel)
                      .map(item => (
                        <KanbanItem id={item._id} onDrop={this.update}>
                          <div style={classes.item}>{item.title}
                            <div style={{ float: "right" }}><FontAwesomeIcon icon={faEdit} onClick={() => this.handleModal(item._id, item.title)} /> <FontAwesomeIcon icon={faDeleteLeft} onClick={e => this.deletedocument(item._id)} /></div>
                          </div>
                        </KanbanItem>
                      ))}
                  </div>

                </div>
              </KanbanColumn>
            ))}

            <Modal size="xl" aria-labelledby="contained-modal-title-vcenter" centered fullscreen show={this.state.show} onHide={() => this.handleModal(0, "")}>
              <ModalHeader closeButton>Task Details</ModalHeader>
              <ModalBody >
                <textarea value={taskDescription} rows={5} onChange={this.handleChange}></textarea>
              </ModalBody>
              <ModalFooter>
                <Button variant="primary" size="sm" onClick={() => this.edit(this.state.selectedId)} color="danger">Update</Button>
                <Button size="sm" onClick={() => this.handleModal(0, "")} color="danger">Close</Button>
              </ModalFooter>
            </Modal>
          </section>
        </main>
      );
    }
  }
}
export default DragDropContext(HTML5Backend)(Kanban);

// Column

const boxTarget = {
  drop(props) {
    return { name: props.status };
  }
};

class KanbanColumn extends React.Component {
  render() {
    return this.props.connectDropTarget(<div>{this.props.children}</div>);
  }
}

KanbanColumn = DropTarget("kanbanItem", boxTarget, (connect, monitor) => ({
  connectDropTarget: connect.dropTarget(),
  isOver: monitor.isOver(),
  canDrop: monitor.canDrop()
}))(KanbanColumn);

// Item

const boxSource = {
  beginDrag(props) {
    return {
      name: props.id
    };
  },

  endDrag(props, monitor) {
    const item = monitor.getItem();
    const dropResult = monitor.getDropResult();
    if (dropResult) {
      props.onDrop(monitor.getItem().name, dropResult.name);
    }
  }
};

class KanbanItem extends React.Component {
  render() {
    return this.props.connectDragSource(<div>{this.props.children}</div>);
  }
}

KanbanItem = DragSource("kanbanItem", boxSource, (connect, monitor) => ({
  connectDragSource: connect.dragSource(),
  isDragging: monitor.isDragging()
}))(KanbanItem);
